function ColorInverter(){this.hexbase="0123456789ABCDEFabcdef";this.HexToR=function(a){var b=parseInt((this.cutHex(a)).substring(0,2),16);return b};this.HexToG=function(a){var b=parseInt((this.cutHex(a)).substring(2,4),16);return b};this.HexToB=function(c){var a=parseInt((this.cutHex(c)).substring(4,6),16);return a};this.cutHex=function(a){var b="";if(a.charAt(0)=="#"){b=a.substring(1,7)}else{b=a}return a};this.DecToHex=function(b){var a=this.hexbase.charAt((b>>4)&15)+this.hexbase.charAt(b&15);return a};this.IsNumeric=function(b){var d;var a=true;var c=0;if(b.length===0){return false}for(c=0;c<b.length&&a===true;c++){d=b.charAt(c);if(this.hexbase.indexOf(d)==-1){a=false}}return a};this.InverseColor=function(d){var f=255-this.HexToR(d);var e=255-this.HexToG(d);var a=255-this.HexToB(d);var c="#"+this.DecToHex(f).toString()+this.DecToHex(e).toString()+this.DecToHex(a).toString();return c}};